import React, { Component } from "react";
import classes from "./Users.module.scss";
import * as axios from "axios";
import userPhoto from "../../../src/assets/images/user.jpeg";

class Users extends Component {
  componentDidMount() {
    axios
      .get("https://social-network.samuraijs.com/api/1.0/users")
      .then(response => this.props.setUsers(response.data.items));
  }

  render() {
    const pagesCount = this.props.totalUsersCount / this.props.pageSize;

    const pages = [];
    for (let i = 1; i <= pagesCount; i++) {
      pages.push(i);
    }

    return (
      <div>
        <div>
          {pages.map(p => {
            <span className={`${classes["page-item"]}` + this.props.currentPage === p ? `${classes["active-mod"]}` : null}>{p}</span>;
          })}
        </div>
        {this.props.users.map(u => (
          <li key={u.id}>
            <div>
              <div>
                <div className={classes["avatar-wrapper"]}>
                  <img
                    src={u.photos.small != null ? u.photos.small : userPhoto}
                    alt=""
                    className={classes["avatar-i"]}
                  />
                </div>
                <div>
                  {u.followed ? (
                    <button
                      onClick={() => {
                        this.props.unfollow(u.id);
                      }}
                    >
                      Unfollow
                    </button>
                  ) : (
                    <button
                      onClick={() => {
                        this.props.follow(u.id);
                      }}
                    >
                      Follow
                    </button>
                  )}
                </div>
              </div>
              <div>
                <div>
                  <div>{u.name}</div>
                  <div>{u.status}</div>
                </div>
                <div>
                  <div>{"u.location.country"}</div>
                  <div>{"u.location.city"}</div>
                </div>
              </div>
            </div>
          </li>
        ))}
      </div>
    );
  }
}

export default Users;
