import React from "react";
import classes from "./Users.module.scss";

const Users = props => {
  if (props.users.length === 0) {
    axios.get("https://social-network.samuraijs.com/api/1.0")
        .then(response => props.setUsers())
  }

  return (
    <div>
      {props.users.map(u => (
        <li key={u.id}>
          <div>
            <div>
              <div className={classes["avatar-wrapper"]}>
                <img src={u.photoUrl} alt="" className={classes["avatar-i"]} />
              </div>
              <div>
                {u.followed ? (
                  <button
                    onClick={() => {
                      props.unfollow(u.id);
                    }}
                  >
                    Unfollow
                  </button>
                ) : (
                  <button
                    onClick={() => {
                      props.follow(u.id);
                    }}
                  >
                    Follow
                  </button>
                )}
              </div>
            </div>
            <div>
              <div>
                <div>{u.fullName}</div>
                <div>{u.status}</div>
              </div>
              <div>
                <div>{u.location.country}</div>
                <div>{u.location.city}</div>
              </div>
            </div>
          </div>
        </li>
      ))}
    </div>
  );
};

export default Users;
