import React from "react";
import classes from "./Users.module.scss";

const Users = props => {
  if (props.users.length === 0) {
    axios.get("https://social-network.samuraijs.com/api/1.0");
    props.setUsers([
      {
        id: 1,
        fullName: "Artem",
        photoUrl:
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVWDHdxyFjEFZr_WVH-igNomgFOr7Pn9LH4y_pRGxihrTxOL--cQ&s",
        followed: true,
        status: "I am a boss",
        location: { country: "Ukraine", city: "Kremenchuk" }
      },
      {
        id: 2,
        fullName: "Thomas",
        photoUrl:
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVWDHdxyFjEFZr_WVH-igNomgFOr7Pn9LH4y_pRGxihrTxOL--cQ&s",
        followed: true,
        status: "I am a teacher",
        location: { country: "England", city: "Liverpool" }
      },
      {
        id: 3,
        fullName: "Roman",
        photoUrl:
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVWDHdxyFjEFZr_WVH-igNomgFOr7Pn9LH4y_pRGxihrTxOL--cQ&s",
        followed: false,
        status: "I am an admin",
        location: { country: "Ukraine", city: "Lviv" }
      },
      {
        id: 4,
        fullName: "Herald",
        photoUrl:
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVWDHdxyFjEFZr_WVH-igNomgFOr7Pn9LH4y_pRGxihrTxOL--cQ&s",
        followed: false,
        status: "I am a witcher",
        location: { country: "Poland", city: "Warsaw" }
      }
    ]);
  }

  return (
    <div>
      {props.users.map(u => (
        <li key={u.id}>
          <div>
            <div>
              <div className={classes["avatar-wrapper"]}>
                <img src={u.photoUrl} alt="" className={classes["avatar-i"]} />
              </div>
              <div>
                {u.followed ? (
                  <button
                    onClick={() => {
                      props.unfollow(u.id);
                    }}
                  >
                    Unfollow
                  </button>
                ) : (
                  <button
                    onClick={() => {
                      props.follow(u.id);
                    }}
                  >
                    Follow
                  </button>
                )}
              </div>
            </div>
            <div>
              <div>
                <div>{u.fullName}</div>
                <div>{u.status}</div>
              </div>
              <div>
                <div>{u.location.country}</div>
                <div>{u.location.city}</div>
              </div>
            </div>
          </div>
        </li>
      ))}
    </div>
  );
};

export default Users;
